import { isEmailValid } from '../helpers/EmailHelper';
import './ValidationError.css';

type ValidationErrorProps = {
    erroMessage: string;
    hasChanged: boolean;
    type: 'required' | 'email';
    value: string;
    testId: string;
}

export default function ValidationError(props: ValidationErrorProps) {
    if (!props.hasChanged) {
        return null;
    }

    const error= <div data-testid={props.testId} className='error'> {props.erroMessage}</div>;

    if (props.type === 'required') {
        return (
            props.value === '' ?
                error
                : null
        )
    }
    return (
        !isEmailValid(props.value)?
            error
            : null
    )
}
